import React, { useState } from "react";
import api from "../services/api";
import { useNavigate } from "react-router-dom";

export default function CreateTask() {
  const [form, setForm] = useState({ title: "", description: "", category: "Personal", deadline: "" });
  const [msg, setMsg] = useState("");
  const navigate = useNavigate();

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    setMsg("");
    try {
      // If backend expects form-data, adjust accordingly.
      const res = await api.post("/tasks/create", form);
      setMsg("Task created");
      setTimeout(() => navigate("/dashboard"), 600);
    } catch (err) {
      setMsg(err.response?.data?.error || "Failed to create");
    }
  };

  return (
    <div style={{ maxWidth: 480 }}>
      <h2>Create Task</h2>
      <form onSubmit={onSubmit}>
        <input name="title" value={form.title} onChange={onChange} placeholder="Title" required /><br/>
        <textarea name="description" value={form.description} onChange={onChange} placeholder="Description" required /><br/>
        <label>
          Category:
          <select name="category" value={form.category} onChange={onChange}>
            <option value="Personal">Personal</option>
            <option value="Office">Office</option>
          </select>
        </label><br/>
        <input name="deadline" type="date" value={form.deadline} onChange={onChange} required /><br/>
        <button type="submit">Create</button>
      </form>
      <p>{msg}</p>
    </div>
  );
}
