import React, { useState, useEffect } from 'react';
import { getTask, updateTask } from '../services/taskService';
import { useNavigate, useParams } from 'react-router-dom';

export default function TaskDetails() {
  const { id } = useParams();
  const [task, setTask] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [form, setForm] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    (async () => {
      try {
        const res = await getTask(id);
        setTask(res.data);
        setForm({ title: res.data.title, description: res.data.description, category: res.data.category, deadline: res.data.deadline?.slice(0,10) });
      } catch (err) {
        console.error(err);
      }
    })();
  }, [id]);

  const save = async () => {
    try {
      await updateTask(id, form);
      navigate('/dashboard');
    } catch (err) {
      console.error(err);
    }
  };

  if (!task) return <p>Loading...</p>;

  return (
    <div className="container mt-5">
      <div className="card p-4 mx-auto" style={{ maxWidth: '500px' }}>
        {!editMode ? (
          <>
            <h2>{task.title}</h2>
            <p>{task.description}</p>
            <small>{task.category} â€” Deadline: {task.deadline?.slice(0,10)}</small>
            <div className="mt-3">
              <button className="btn btn-primary me-2" onClick={() => setEditMode(true)}>Edit</button>
              <button className="btn btn-secondary" onClick={() => navigate('/dashboard')}>Back</button>
            </div>
          </>
        ) : (
          <>
            <div className="mb-3">
              <input type="text" className="form-control" value={form.title} onChange={(e) => setForm({ ...form, title: e.target.value })} />
            </div>
            <div className="mb-3">
              <textarea className="form-control" value={form.description} onChange={(e) => setForm({ ...form, description: e.target.value })} />
            </div>
            <div className="mb-3">
              <select className="form-select" value={form.category} onChange={(e) => setForm({ ...form, category: e.target.value })}>
                <option>Personal</option>
                <option>Office</option>
              </select>
            </div>
            <div className="mb-3">
              <input type="date" className="form-control" value={form.deadline} onChange={(e) => setForm({ ...form, deadline: e.target.value })} />
            </div>
            <button className="btn btn-primary me-2" onClick={save}>Save</button>
            <button className="btn btn-secondary" onClick={() => setEditMode(false)}>Cancel</button>
          </>
        )}
      </div>
    </div>
  );
}
