import React, { useEffect, useState } from "react";
import { getAllTasks, deleteTask, filterTasks } from "../services/taskService";
import TaskCard from "../components/TaskCard";
import { useNavigate } from "react-router-dom";
import {
  Box,
  Button,
  Typography,
  Select,
  MenuItem,
  Grid,
  Alert,
  InputLabel,
  FormControl,
} from "@mui/material";

export default function Dashboard() {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const loadTasks = async () => {
    try {
      const res = filter ? await filterTasks(filter) : await getAllTasks();
      setTasks(res.data);
    } catch (err) {
      setError(err?.response?.data?.error || "Could not fetch tasks");
    }
  };

  useEffect(() => {
    loadTasks();
  }, [filter]);

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this task?")) return;
    await deleteTask(id);
    loadTasks();
  };

  return (
    <Box sx={{ p: 3 }}>
      {/* Header */}
      <Box
        sx={{
          display: "flex",
          justifyContent: "space-between",
          mb: 3,
          alignItems: "center",
        }}
      >
        <Typography variant="h5" fontWeight="bold">
          Your Tasks
        </Typography>

        <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
          <FormControl size="small" sx={{ minWidth: 150 }}>
            <InputLabel>Filter</InputLabel>
            <Select
              value={filter}
              label="Filter"
              onChange={(e) => setFilter(e.target.value)}
            >
              <MenuItem value="">All</MenuItem>
              <MenuItem value="Personal">Personal</MenuItem>
              <MenuItem value="Office">Office</MenuItem>
            </Select>
          </FormControl>

          <Button
            variant="contained"
            color="primary"
            onClick={() => navigate("/task/new")}
          >
            New Task
          </Button>
        </Box>
      </Box>

      {/* Error */}
      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {/* Task List */}
      {tasks.length === 0 ? (
        <Typography>No tasks yet.</Typography>
      ) : (
        <Grid container spacing={2}>
          {tasks.map((task) => (
            <Grid
              item
              xs={12}
              sm={6}
              md={4}
              key={task._id}
              sx={{ display: 'flex' }}
            >
              <TaskCard task={task} onDelete={handleDelete} />
            </Grid>
          ))}
        </Grid>
      )}
    </Box>
  );
}
