import React, { useEffect, useState } from "react";
import { api } from "../services/api";
import { Link } from "react-router-dom";

export default function Dashboard() {
  const [tasks, setTasks] = useState([]);
  const [category, setCategory] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const load = async (cat = "") => {
    setLoading(true);
    setError("");
    try {
      // your backend might provide /tasks or /tasks/filter
      const url = cat ? "/tasks/filter" : "/tasks/all";
      const res = await api.get(url, { params: cat ? { category: cat } : {} });
      // backend might return array in res.data
      setTasks(res.data);
    } catch (err) {
      setError(err.response?.data?.error || "Failed to load tasks");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load(category);
  }, [category]);

  return (
    <div style={{ maxWidth: 900 }}>
      <h2>Dashboard</h2>

      <label>
        Category:
        <select value={category} onChange={e => setCategory(e.target.value)}>
          <option value="">All</option>
          <option value="Personal">Personal</option>
          <option value="Office">Office</option>
        </select>
      </label>
      {" "}
      <Link to="/create">+ Create Task</Link>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      <ul>
        {tasks && tasks.map(t => (
          <li key={t._id || t.id} style={{ marginBottom: 8 }}>
            <strong>{t.title}</strong> <em>({t.category})</em><br />
            <small>{t.description}</small><br />
            <small>Deadline: {new Date(t.deadline).toLocaleDateString?.() || t.deadline}</small>
          </li>
        ))}
      </ul>
    </div>
  );
}
