import { api } from "./api";

export const createTask = (taskData) => api.post("/tasks/create", taskData);
export const getAllTasks = () => api.get("/tasks/all");
export const getTask = (task_id) => api.get(`/tasks/${task_id}`);
export const filterTasks = (filterQuery) => api.get(`/tasks/filter?${filterQuery}`);
export const updateTask = (task_id, taskData) => api.post(`/tasks/update/${task_id}`, taskData);
export const deleteTask = (task_id) => api.post(`/tasks/delete/${task_id}`);
