import api from './api';

export const createTask = (payload) => api.post('/tasks/create', payload);
export const getAllTasks = () => api.get('/tasks/all');
export const getTask = (id) => api.get(`/tasks/${id}`);
export const updateTask = (id, payload) => api.post(`/tasks/update/${id}`, payload);
export const deleteTask = (id) => api.post(`/tasks/delete/${id}`);
export const filterTasks = (category) => api.get(`/tasks/filter?category=${category}`);
