import React, { useEffect, useState } from 'react';
import { getAllTasks, deleteTask, filterTasks } from '../services/taskService';
import TaskCard from '../components/TaskCard';
import { useNavigate } from 'react-router-dom';

export default function Dashboard() {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const loadTasks = async () => {
    try {
      const res = filter ? await filterTasks(filter) : await getAllTasks();
      setTasks(res.data);
    } catch (err) {
      setError(err?.response?.data?.error || 'Could not fetch tasks');
    }
  };

  useEffect(() => { loadTasks(); }, [filter]);

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this task?')) return;
    await deleteTask(id);
    loadTasks();
  };

  return (
    <div>
      <div className="d-flex justify-content-between mb-3">
        <h2>Your Tasks</h2>
        <div>
          <select value={filter} onChange={(e) => setFilter(e.target.value)} className="form-select d-inline-block me-2" style={{ width: '150px' }}>
            <option value="">All</option>
            <option value="Personal">Personal</option>
            <option value="Office">Office</option>
          </select>
          <button className="btn btn-primary" onClick={() => navigate('/task/new')}>New Task</button>
        </div>
      </div>
      {error && <div className="alert alert-danger">{error}</div>}
      {tasks.length === 0 ? <p>No tasks yet.</p> :
        tasks.map(task => <TaskCard key={task._id} task={task} onDelete={handleDelete} />)
      }
    </div>
  );
}
