import React, { useState } from 'react';
import { createTask } from '../services/taskService';
import { useNavigate } from 'react-router-dom';

export default function TaskForm() {
  const [form, setForm] = useState({ title: '', description: '', category: 'Personal', deadline: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await createTask(form);
      navigate('/dashboard');
    } catch (err) {
      setError(err?.response?.data?.error || 'Could not create task');
    }
  };

  return (
    <div className="container mt-5">
      <div className="card p-4 mx-auto" style={{ maxWidth: '500px' }}>
        <h2 className="mb-3">New Task</h2>
        {error && <div className="alert alert-danger">{error}</div>}
        <form onSubmit={submit}>
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Title"
              value={form.title}
              onChange={(e) => setForm({ ...form, title: e.target.value })}
            />
          </div>
          <div className="mb-3">
            <textarea
              className="form-control"
              placeholder="Description"
              value={form.description}
              onChange={(e) => setForm({ ...form, description: e.target.value })}
            />
          </div>
          <div className="mb-3">
            <select className="form-select" value={form.category} onChange={(e) => setForm({ ...form, category: e.target.value })}>
              <option>Personal</option>
              <option>Office</option>
            </select>
          </div>
          <div className="mb-3">
            <input type="date" className="form-control" value={form.deadline} onChange={(e) => setForm({ ...form, deadline: e.target.value })} />
          </div>
          <button type="submit" className="btn btn-primary me-2">Create</button>
          <button type="button" className="btn btn-secondary" onClick={() => navigate('/dashboard')}>Cancel</button>
        </form>
      </div>
    </div>
  );
}
